# -*- coding: utf-8 -*-\nimport json\nimport os\n\n# 创建输出目录\nos.makedirs("药品性状", exist_ok=True)\n\n# 读取JSON文件\nwith open("full_drug_standards.json", "r", encoding="utf-8") as f:\n    drugs_data = json.load(f)\n\n# 抽取指定数量的药品\nextracted_count = 0\nfor drug in drugs_data:\n    if extracted_count >= 20:\n        break\n        \n    # 获取药品名称和性状\n    name = drug.get("名称", "未知药品")\n    property_desc = drug.get("性状", "未找到")\n    \n    # 创建单个药品的JSON数据\n    drug_property = {\n        "名称": name,\n        "性状": property_desc\n    }\n    \n    # 生成安全的文件名（移除可能引起问题的字符）\n    safe_name = "".join(c for c in name if c.isalnum() or c in (" ", "_", "-")).rstrip()\n    # 如果文件名为空，则使用索引\n    if not safe_name:\n        safe_name = f"drug_{extracted_count}"\n        \n    # 写入单独的JSON文件\n    output_file = os.path.join("药品性状", f"{safe_name}.json")\n    with open(output_file, "w", encoding="utf-8") as f:\n        json.dump(drug_property, f, ensure_ascii=False, indent=2)\n    \n    extracted_count += 1\n\nprint(f"成功抽取{extracted_count}个药品的性状信息并保存到"药品性状"目录下")
