# -*- coding: utf-8 -*-\nimport json\n\n# 尝试读取JSON文件的前几个元素\ntry:\n    with open("full_drug_standards.json", "r", encoding="utf-8") as f:\n        # 读取前1000个字符来查看文件结构\n        sample = f.read(1000)\n        print("文件前1000个字符:")\n        print(sample)\n        \n        # 重置文件指针\n        f.seek(0)\n        \n        # 尝试加载整个文件\n        print("\\n尝试加载整个JSON文件...")\n        data = json.load(f)\n        print(f"JSON文件加载成功，类型: {type(data)}")\n        \n        if isinstance(data, list):\n            print(f"数组长度: {len(data)}")\n            if len(data) > 0:\n                first_item = data[0]\n                print(f"第一个元素类型: {type(first_item)}")\n                print("第一个元素的键:")\n                for key in first_item.keys():\n                    print(f"  {key}")\n                    \n                # 检查是否有"名称"和"性状"字段\n                name = first_item.get("名称", "未找到名称字段")\n                property_desc = first_item.get("性状", "未找到性状字段")\n                print(f"\\n第一个药品名称: {name}")\n                print(f"第一个药品性状: {property_desc[:100]}...")  # 只显示前100个字符\n        elif isinstance(data, dict):\n            print("文件是一个字典")\n            print("字典的键:")\n            for key in data.keys():\n                print(f"  {key}")\n        else:\n            print(f"未知的数据类型: {type(data)}")\n            \nexcept Exception as e:\n    print(f"处理过程中出现错误: {e}")
